mapper.select:
select a.id, u1.name as owner, a.name, a.start_date, a.end_date, a.cost, a.description, a.create_time, u2.name as create_by, a.edit_time, u3.name as edit_by from tbl_activity a join tbl_user u1 on a.owner = u1.id join tbl_user u2 on a.create_by = u2.id left join tbl_user u3 on a.edit_by = u3.id
and u1.name like concat('%',#{owner},'%')
and a.name like concat('%',#{name},'%')
and a.start_date >= #{startDate}
and a.end_date <= #{endDate}
order by a.create_time desc limit #{startNo}, #{pageSize}

select count(*) from tbl_activity a join tbl_user u1 on a.owner = u1.id join tbl_user u2 on a.create_by = u2.id left join tbl_user u3 on a.edit_by = u3.id
and u1.name like concat('%',#{owner},'%')
and a.name like concat('%',#{name},'%')
and a.start_date >= #{startDate}
and a.end_date <= #{endDate}

select a.id, u1.name as owner, a.name, a.start_date, a.end_date, a.cost, a.description, a.create_time, u2.name as create_by, a.edit_time, u3.name as edit_by from tbl_activity a join tbl_user u1 on a.owner = u1.id join tbl_user u2 on a.create_by = u2.id left join tbl_user u3 on a.edit_by = u3.id where a.id in
#{id}
order by a.create_time desc

