mapper.select:
select t.id, t.name, cus.name as customer_id, dv1.value as stage, dv2.value as type, u.name as owner, dv3.value as source, con.fullname as contacts_id from tbl_transaction t inner join tbl_customer cus on cus.id = t.customer_id inner join tbl_dic_value dv1 on dv1.id = t.stage left join tbl_dic_value dv2 on dv2.id = t.type inner join tbl_user u on u.id = t.owner left join tbl_dic_value dv3 on dv3.id = t.source left join tbl_contacts con on con.id = t.contacts_id
and u.name like concat('%', #{owner}, '%')
and t.name like concat('%', #{name}, '%')
and cus.name like concat('%', #{customerName}, '%')
and dv1.id = #{stage}
and dv2.id = #{type}
and dv3.id = #{source}
and con.fullname like concat('%', #{contactsName}, '%')
order by t.create_time desc limit #{startNo}, #{pageSize}

select count(*) from tbl_transaction t inner join tbl_customer cus on cus.id = t.customer_id inner join tbl_dic_value dv1 on dv1.id = t.stage left join tbl_dic_value dv2 on dv2.id = t.type inner join tbl_user u on u.id = t.owner left join tbl_dic_value dv3 on dv3.id = t.source left join tbl_contacts con on con.id = t.contacts_id
and u.name like concat('%', #{owner}, '%')
and t.name like concat('%', #{name}, '%')
and cus.name like concat('%', #{customerName}, '%')
and dv1.id = #{stage}
and dv2.id = #{type}
and dv3.id = #{source}
and con.fullname like concat('%', #{contactsName}, '%')

